Aplica_vallidaciones.inputs:
@'generadorLotesDominio = new Sat.Scade.PagosCore.Dominio.GeneradorLotesDominio();
lbELotesCeros = recepcionArchivos.TipoProceso == Sat.Scade.Pagos.Modelo.Comun.TipoProceso.Ceros;

if(lbELotesCeros){
    lsResultadoValidacion = generadorLotesDominio.ValidarLoteCeros(recepcionArchivos.FechaContingenciaCeros,procesoExterno.IdProcesoPago, procesoExterno.IdProcesoExterno, esReenvio, cuentaBloques);
}else{
    lsResultadoValidacion = generadorLotesDominio.ValidarLotePagos(recepcionArchivos.IdProceso, cuentaBloques);
}

lbHayErrores = lsResultadoValidacion.IndexOf("<Error>") > 0;

