Construye_el_archivo_86bf7cc1.inputs:
@'System.Diagnostics.EventLog.WriteEntry("LotePago","Construye archivo de operaciones");

//generar lote
loArchivoLoteDominio.GenerarArchivoLote(lsResultadoValidacion, procesoExterno,  System.Convert.ToInt16(cuentaBloques), recepcionArchivos);

//registra en control ceros
if(lbELotesCeros){
    loControlCeros = new Sat.Scade.PagosCore.Dominio.ControlLotesCerosDominio();
    loControlCeros.InsertarControlCeros(procesoExterno.IdProcesoPago, procesoExterno.IdProcesoExterno, cuentaBloques, lsResultadoValidacion, recepcionArchivos.FechaContingenciaCeros);
}
System.Diagnostics.EventLog.WriteEntry("LotePago","Fin Construye archivo de operaciones")'

Actualiza_estado.inputs:
@'System.Diagnostics.EventLog.WriteEntry("LotePago","Actualización estado ");
loControl.IdArchivoNepe = System.Convert.ToInt16(cuentaBloques);
loControl.IdProcesoExterno = procesoExterno.IdProcesoExterno;
loControl.IdProcesoPago = procesoExterno.IdProcesoPago;

if(lbELotesCeros){
    loControl.IdTipoLote = Sat.Scade.Pagos.Modelo.Comun.TiposLote.Ceros;
}else{
    loControl.IdTipoLote = Sat.Scade.Pagos.Modelo.Comun.TiposLote.Pagos;
}

//Actualizar el estado en la tabla de control, fin con errores o fin sin errores
if(lbHayErrores){
    loControl.IdEstatusGenLote = Sat.Scade.Pagos.Modelo.Comun.EstatusGeneracionLotes.TerminadoConErrores;
    loControl.RutaArchivo = null;
}else{
    loControl.IdEstatusGenLote = Sat.Scade.Pagos.Modelo.Comun.EstatusGeneracionLotes.TerminadoSinErrores;
}
loteCompleto = 0;
//0=incompleto
//1=completo sin errores
//2=completo con errores
loteCompleto = loErroresLote.ActualizarControlGeneracionLotes(loControl);

//si error y cero, borrar control ceros
if(lbELotesCeros && lbHayErrores){
    loControlCeros.BorraControlCeros(procesoExterno.IdProcesoPago, procesoExterno.IdProcesoExterno);
}
System.Diagnostics.EventLog.WriteEntry("LotePago","Fin Actualización estado ")'

Genera_Lote_57a6d3c0.inputs:
@'System.Diagnostics.EventLog.WriteEntry("Pagos","Genera archivo cifras");
loArchivoLoteDominio.GenerarArchivoCifras(procesoExterno, recepcionArchivos);
loControl = loErroresLote.ConsultarControl(procesoExterno.IdProcesoPago, procesoExterno.IdProcesoExterno, System.Convert.ToInt16(cuentaBloques));
lbCifrasGeneradoOk = loControl.IdEstatusGenLote == Sat.Scade.Pagos.Modelo.Comun.EstatusGeneracionLotes.TerminadoSinErrores;

if(lbCifrasGeneradoOk){
    procesoPago = new Sat.Scade.Pagos.Modelo.Comun.ProcesosPagos();
    procesoPago.IdProcesoPago = procesoExterno.IdProcesoPago;
    recepcionArchivos.Estado = Sat.Scade.Pagos.Modelo.Comun.EstatusProcesosPagos.TerminaProceso;
    procesoPago.EstadoProcesoPago = recepcionArchivos.Estado;

    lbELotesCeros = recepcionArchivos.TipoProceso == Sat.Scade.Pagos.Modelo.Comun.TipoProceso.Ceros;

/*
    if(lbELotesCeros){
        procesoPago.TipoProceso = Sat.Scade.Pagos.Modelo.Comun.TipoProceso.Ceros;
    }else{
        procesoPago.TipoProceso = Sat.Scade.Pagos.Modelo.Comun.TipoProceso.Pagos;
    }
*/

    procesoPago.TipoProceso = recepcionArchivos.TipoProceso;
    procesoPagoDominio = new Sat.Scade.PagosCore.Dominio.ProcesosPagosDominio();
    System.Diagnostics.EventLog.WriteEntry("Pagos","Inicio Actualizar proceso pago: " + procesoPago.IdProcesoPago.ToString());
    System.Diagnostics.EventLog.WriteEntry("Pagos","Estado proceso pago: " + procesoPago.EstadoProcesoPago.ToString());
    procesoPagoDominio.ActualizarEstado(procesoPago);
    System.Diagnostics.EventLog.WriteEntry("Pagos","Fin Actualizar proceso pago: " + procesoPago.IdProcesoPago.ToString());
}

System.Diagnostics.EventLog.WriteEntry("Pagos","Fin Genera archivo cifras")'

